import React from 'react'
import User from '../../assets/user.svg'


const Dashboard = () => {
  const contacts =[
    {
      name:"Ved",
      status:"Available",
      img:User
    },
    {
      name:"Diya",
      status:"Available",
      img:User
    },
    {
      name:"Zayn",
      status:"Available",
      img:User
    },
    {
      name:"Abhimanyu",
      status:"Available",
      img:User
    },
    {
      name:"Diksha",
      status:"Available",
      img:User
    },
    
    
  ]
  return (
    <div className='w-screen flex'>
    <div className='w-[25%] h-screen bg-gray-100'>
     <div className='flex  items-center my-8 mx-14  '>
     <div className='border border-black p-[4px] rounded-full '><img src ={User} /></div>
     <div className='ml-2 py-1'>
        <h3 className='text-2xl'>Sakshi</h3>
        <p className='text-lg font-light text-primary'>My Account</p>
     </div>
     </div>
     <hr />
     <div className='mx-6 mt-5'>
      <div className='text-bold ml-2 text-lg text-primary'>
        Messages
      </div>
      <div>
        {
          contacts.map(({name,status,img}) =>{
            return(
              <div className='flex  items-center py-4 border-b border-b-gray-300'>
                <div className='cursor-pointer flex items-center'>
             <div className='border border-black p-[1px] rounded-full'><img src ={img} width={40} heigth={40}/></div>
              <div className='ml-1'>
                   <h3 className='text-lg font-semi-bold'>{name}</h3>
                    <p className='text-sm font-light text-primary'>{status}</p>
                     </div>
                     </div>
             </div>
            )
          })
        }
      </div>
     </div>
    </div>
    <div className='w-[50%] bg-white h-screen'></div>
    <div className='w-[25%]  h-screen'></div>
    </div>
  )
}

export default Dashboard
