import './App.css';
import Dashboard from './modules/Dashboard';
import Form from './modules/form';
import { Routes, Route, Navigate } from 'react-router-dom';

const ProtectedRoute = ({ children, auth = false }) => {
  const isLoggedIn = localStorage.getItem("user:token") !== null;

  console.log("isLoggedIn:", isLoggedIn);
  console.log("auth required for this route:", auth);
  console.log("current path:", window.location.pathname);

  // If not logged in and auth is required, redirect to sign-in
  if (!isLoggedIn && auth) {
    console.log("Redirecting to sign-in");
    return <Navigate to={'/users/sign-in'} />;
  }

  // If logged in and trying to access sign-in or sign-up, redirect to dashboard
  if (isLoggedIn && ['/users/sign-in', '/users/sign-up'].includes(window.location.pathname)) {
    console.log("Redirecting to dashboard");
    return <Navigate to={'/'} />;
  }

  // If conditions are met, render children
  return children;
};

function App() {
  return (
    <Routes>
      <Route
        path='/'
        element={
          <ProtectedRoute auth={true}>
            <Dashboard />
          </ProtectedRoute>
        }
      />
      <Route
        path='/users/sign-in'
        element={
          <ProtectedRoute>
            <Form isSignInPage={true} />
          </ProtectedRoute>
        }
      />
      <Route
        path='/users/sign-up'
        element={
          <ProtectedRoute>
            <Form isSignInPage={false} />
          </ProtectedRoute>
        }
      />
    </Routes>
  );
}

export default App;
