import React from 'react'
import User from '../../assets/user.svg'


const Dashboard = () => {
  const contacts =[
    {
      name:"John",
      status:"Available",
      img:User
    },
    {
      name:"Diya",
      status:"Available",
      img:User
    },
    {
      name:"Zayn",
      status:"Available",
      img:User
    },
    {
      name:"Abhimanyu",
      status:"Available",
      img:User
    },
    {
      name:"Diksha",
      status:"Available",
      img:User
    },
    
  ]
  return (
    <div className='w-screen flex'>
    <div className='w-[25%] h-screen border border-black bg-white'>
     <div className='flex justify-center items-center my-8'>
     <div className='border border-black p-[4px] rounded-full'><img src ={User}/></div>
     <div className='ml-2'>
        <h3 className='text-2xl'>Sakshi</h3>
        <p className='text-lg font-light'>My Account</p>
     </div>
     </div>
     <hr/>
     <div>
      <div className='text-bold ml-4 text-lg'>
        Messages
      </div>
      <div>
        {
          contacts.map(({name,status,img}) =>{
            return(
              <div className='flex  items-center my-4'>
             <div className='border border-black p-[2px] rounded-full'><img src ={img}/></div>
              <div className='ml-2'>
                   <h3 className='text-lg'>{name}</h3>
                    <p className='text-sm font-light'>{status}</p>
                     </div>
             </div>
            )
          })
        }
      </div>
     </div>
    </div>
    <div className='w-[50%] border border-black h-screen'></div>
    <div className='w-[25%] border border-black h-screen'></div>
    </div>
  )
}

export default Dashboard
