import Button from "../../components/Buttons";
import Input from "../../components/input";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Form = ({
  isSignInPage = true,
}) => {
  const [data, setData] = useState({
    ...(!isSignInPage && {
      fullName: ""
    }),
    email: "",
    password: ""
  });

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload
    console.log("data:>>", data);
    
    const res = await fetch(`http://localhost:8000/api/${isSignInPage ? "login" : "register"}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    });
    const resData = await res.json();
    console.log("response data:>>", resData);
  };

  return (
    <div className="bg-light h-screen flex items-center justify-center">
      <div className="bg-white w-[600px] h-[550px] shadow-lg rounded-lg flex flex-col justify-center items-center mx-auto my-auto">
        <div className="text-3xl font-bold">
          Welcome {isSignInPage && " back"}
        </div>
        <div className="text-xl font-light mb-14">
          {isSignInPage ? "Sign in to explore" : "Sign up now to get started"}
        </div>
        <form className="flex flex-col items-center w-[65%]" onSubmit={handleSubmit}>
          {!isSignInPage && (
            <Input
              label="Full Name"
              name="fullName"
              placeholder="Enter your full name"
              className="mb-6 w-[50%]"
              value={data.fullName}
              onChange={(e) => setData({ ...data, fullName: e.target.value })}
            />
          )}
          <Input
            label="Email Address"
            name="email"
            type="email"
            placeholder="Enter your email"
            className="mb-6 w-[50%]"
            value={data.email}
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
          <Input
            label="Password"
            name="password"
            type="password"
            placeholder="Enter your password"
            className="mb-14 w-[50%]"
            value={data.password}
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
          <Button
            label={isSignInPage ? "Sign in" : "Sign up"}
            type="submit"
            className="w-full mb-2"
          />
        </form>
        <div>
          {isSignInPage ? "Don't have an account?" : "Already have an account?"}{" "}
          <span
            className="text-primary cursor-pointer underline"
            onClick={() => navigate(`/users/${isSignInPage ? "sign-up" : "sign-in"}`)}
          >
            {isSignInPage ? "Sign up" : "Sign in"}
          </span>
        </div>
      </div>
    </div>
  );
};

export default Form;
