import Button from "../../components/Buttons"
import Input from "../../components/input"
import React from 'react'

const Form = ({
  isSignInPage = true,
}) => {
  return (
    <div className="bg-white w-[600px] h-[550px] shadow-lg rounded-lg flex flex-col justify-center items-center mx-auto my-auto">
    <div className="text-3xl font-bold">Welcome {isSignInPage && "back"}</div>
    <div className="text-xl font-light mb-14">{isSignInPage?"Sign in to explore":"Sign up now to get started"}</div>
   {!isSignInPage && <Input label="Fullname" name="name" placeholder="Enter your Full name"/>}
    <Input label="Email address" name="email" placeholder="Enter your email"/>
    <Input label="Password" type="password" name="password" placeholder="Enter your Password" />
    <Button label={isSignInPage ? "Sign in":"Sign up"} className="w-1/2 mb-2" />
    <div>{isSignInPage ? "Didn't have an account?":"Already have an account?"}<span className="text-primary cursor-pointer underline">{isSignInPage?"Sign-up":"Sign-in"}</span></div>
    </div>
  )
}

export default Form
